    this.cardTimeLine = gsap.timeline();
    this.initGsapAnimations();
    gsap.to(this.$refs.container, { opacity: 1, stagger: 0.1, duration: 0.5 });
  },
  
  methods: {
    initGsapAnimations() {
        gsap.fromTo(".card",
        {
          opacity:0,
          y:50,
        },
        {
          opacity:1,
          y:0,
          duration:.1,
          stagger:{each:'0.1'},
          ease: 'power2.out'
        })
        gsap.from(".rating", {
          textContent: 0,
          duration: .1,
          ease: "power1.inOut",
          delay:1,
          stagger:{each:'0.1'},
          //snap: { textContent: 1 },
        });
    },
    onLeave(index) {
      const cardHeading = document.getElementById(`card${index}`);
      const icon = cardHeading.querySelector(".icon");
      const name = cardHeading.querySelector(".name");
      const description = document.getElementById(`description${index}`);
      // Animate to `flex-col` layout
      if(window.innerWidth > 1024){
      gsap.to(icon.children, {
        width:"100px"
      })
      gsap.to(icon, {
        y: 0, 
        x: 0,
        duration: .5,
        width:"100px",
        force3D: true,
        ease: "power2.out"
      });
      gsap.to(name, {
        y: 0,
        x: 0,
        duration: 0.5,
        force3D: true,
        ease: "power2.out"
      });

      gsap.to(description, {
        opacity:0,
        display:"none",
        y:0,
        duration:0.5,
        ease:"power2.out"
      })
      gsap.to(cardHeading, {
        flexDirection: "column",
        duration: 0.5,
        ease: "power2.out"
      });

      }
    },
    onHover(index) {
      const cardHeading = document.getElementById(`card${index}`);
      const icon = cardHeading.querySelector(".icon");
      const name = cardHeading.querySelector(".name");
      const description = document.getElementById(`description${index}`);
      if(window.innerWidth > 1024){
        gsap.to(icon.children, {
        width:"52px"
      })
      // Reset to `flex-row` layout
      gsap.to(icon, {
        y: -30,
        x: 0,
        duration: 0.5,
        width:"45px",
        force3D: true,
        ease: "power2.out"
      });
      gsap.to(name, {
        y: -30,
        x: 0,
        fontSize:"24px",
        textAlign:"left",
        duration: 0.5,
        force3D: true,
        ease: "power2.out",
        // transform: 'translate3d(-20px, -25px, 10px)'
      });

      gsap.to(description, {
        opacity:1,
        display:"flex",
        y:-58,
        duration:.5,
        ease:"power2.out",
      })
      gsap.to(cardHeading, {
        flexDirection: "row",
        // alignItems: "center",
        duration: 0.5,
        ease: "power2.out"
      });
      }
      
    }
